import React, { useState } from "react";

function App() {
  const [ptsHome, setPtsHome] = useState("");
  const [ptsAway, setPtsAway] = useState("");
  const [prediction, setPrediction] = useState(null);

  const handlePredict = () => {
    fetch("http://localhost:8000/predict", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        pts_home: parseFloat(ptsHome),
        pts_away: parseFloat(ptsAway),
      }),
    })
      .then((res) => res.json())
      .then((data) => setPrediction(data));
  };

  return (
    <div style={{ padding: "2rem" }}>
      <h1>NBA Winner Predictor</h1>
      <input
        type="number"
        placeholder="Home team points"
        value={ptsHome}
        onChange={(e) => setPtsHome(e.target.value)}
      />
      <br />
      <input
        type="number"
        placeholder="Away team points"
        value={ptsAway}
        onChange={(e) => setPtsAway(e.target.value)}
      />
      <br />
      <button onClick={handlePredict}>Predict Winner</button>

      {prediction && (
        <div style={{ marginTop: "1rem" }}>
          <h3>{prediction.prediction}</h3>
          <p>Confidence: {(prediction.confidence * 100).toFixed(2)}%</p>
        </div>
      )}
    </div>
  );
}

export default App;
