import React, { useState, useEffect } from "react";
import "./App.css";

function App() {
  const [games, setGames] = useState([]);
  const [selectedGame, setSelectedGame] = useState(null);
  const [prediction, setPrediction] = useState(null);
  const [sportKey, setSportKey] = useState("basketball_nba");
  const [chatGPTAnalysis, setChatGPTAnalysis] = useState(null);
  const [predictionLoading, setPredictionLoading] = useState(false);
  const [modelStatus, setModelStatus] = useState(null);

  // Check model status on load
  useEffect(() => {
    fetch("http://localhost:8000/model/status")
      .then((res) => res.json())
      .then((data) => {
        console.log("Model status:", data);
        setModelStatus(data);
      })
      .catch((err) => console.error("Failed to fetch model status", err));
  }, []);

  useEffect(() => {
    setSelectedGame(null);
    setPrediction(null);
    setChatGPTAnalysis(null);

    fetch(`http://localhost:8000/games/${sportKey}`)
      .then((res) => res.json())
      .then((data) => setGames(data))
      .catch((err) => console.error("Failed to fetch games", err));
  }, [sportKey]);

  const handlePredict = () => {
    if (!selectedGame) {
      console.warn("No game selected.");
      return;
    }

    setPredictionLoading(true);
    setPrediction(null);

    // FIXED: Send correct data format for ESPN ML model
    const requestData = {
      sport: sportKey, // Add sport parameter
      team_1: selectedGame.team_1, // Home team from odds API
      team_2: selectedGame.team_2, // Away team from odds API
      // Keep backward compatibility
      team_id_home: selectedGame.team_1,
      team_id_away: selectedGame.team_2,
      fg_pct_home: 45.0,
      reb_home: 43,
      ast_home: 25,
      fg_pct_away: 45.0,
      reb_away: 43,
      ast_away: 25,
    };

    console.log("ðŸŽ¯ Sending ESPN ML prediction request:", requestData);

    fetch("http://localhost:8000/predict", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(requestData),
    })
      .then((res) => res.json())
      .then((data) => {
        console.log("âœ… ESPN ML prediction response:", data);
        setPrediction(data);
      })
      .catch((err) => {
        console.error("âŒ Prediction failed:", err);
        setPrediction({
          error: "Prediction failed: " + err.message,
          confidence: 0.5,
          home_win_probability: 0.5,
          away_win_probability: 0.5,
        });
      })
      .finally(() => {
        setPredictionLoading(false);
      });
  };

  const handleChatGPTAnalysis = () => {
    if (!selectedGame) {
      console.warn("No game selected.");
      return;
    }

    // Enhanced prompt with ML prediction data
    let prompt = `Game Analysis:
${selectedGame.team_1} vs ${selectedGame.team_2} (${sportKey
      .replace("_", " ")
      .toUpperCase()}).
Odds: ${selectedGame.team_1} ${selectedGame.odds?.[selectedGame.team_1]}, ${
      selectedGame.team_2
    } ${selectedGame.odds?.[selectedGame.team_2]}.`;

    if (selectedGame.spreads) {
      prompt += `
Spread: ${selectedGame.team_1} ${
        selectedGame.spreads?.[selectedGame.team_1]?.point ?? "N/A"
      } (${selectedGame.spreads?.[selectedGame.team_1]?.price ?? "N/A"}), ${
        selectedGame.team_2
      } ${selectedGame.spreads?.[selectedGame.team_2]?.point ?? "N/A"} (${
        selectedGame.spreads?.[selectedGame.team_2]?.price ?? "N/A"
      }).`;
    }

    // Add ML prediction if available
    if (prediction && !prediction.error) {
      prompt += `

ðŸ¤– ESPN AI MODEL PREDICTION:
- Predicted Winner: ${prediction.prediction}
- Confidence: ${(prediction.confidence * 100).toFixed(1)}%
- Home Win Probability: ${(prediction.home_win_probability * 100).toFixed(1)}%
- Away Win Probability: ${(prediction.away_win_probability * 100).toFixed(1)}%
- Model: ${prediction.model_info?.type || "ESPN Ensemble"}
- Trained on: 6,717+ games from ESPN API`;
    }

    prompt += `

Should I bet on this game? Please analyze both the odds and the AI prediction to give betting advice.`;

    console.log("ðŸ“ Prompt sent to ChatGPT:", prompt);

    fetch("http://localhost:8000/analyze-bet/", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ prompt }),
    })
      .then((res) => res.json())
      .then((data) => {
        console.log("ðŸ’¬ ChatGPT response:", data);
        setChatGPTAnalysis(data.analysis || data.error || "No response");
      })
      .catch((err) => {
        console.error("Error contacting ChatGPT:", err);
        setChatGPTAnalysis("Error contacting ChatGPT.");
      });
  };

  const trainModel = () => {
    console.log("ðŸ¤– Training ESPN ML model...");

    if (!window.confirm("Training will take several minutes. Continue?")) {
      return;
    }

    fetch("http://localhost:8000/model/train", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
    })
      .then((res) => res.json())
      .then((data) => {
        console.log("Training result:", data);
        alert(`Training ${data.status}: ${data.message}`);

        // Refresh model status
        fetch("http://localhost:8000/model/status")
          .then((res) => res.json())
          .then((statusData) => setModelStatus(statusData));
      })
      .catch((err) => {
        console.error("Training failed:", err);
        alert("Training failed: " + err.message);
      });
  };

  const getModelStatusColor = () => {
    if (!modelStatus) return "#6c757d";
    if (modelStatus.model_available && modelStatus.trained_sports?.length > 0)
      return "#28a745";
    if (modelStatus.model_available) return "#ffc107";
    return "#dc3545";
  };

  const getModelStatusText = () => {
    if (!modelStatus) return "Loading...";
    if (modelStatus.model_available && modelStatus.trained_sports?.length > 0) {
      return `Ready (${modelStatus.trained_sports.length} sports)`;
    }
    if (modelStatus.model_available) return "Needs Training";
    return "Not Available";
  };

  return (
    <div style={{ padding: "2rem", fontFamily: "Arial" }}>
      {/* Navigation Bar */}
      <nav
        style={{
          backgroundColor: "#FFFFFF",
          padding: "1rem",
          color: "white",
          display: "flex",
          alignItems: "center",
          gap: "1rem",
          position: "sticky",
          top: 0,
          zIndex: 1000,
          borderBottom: "2px solid #007bff",
        }}
      >
        <button
          style={{
            backgroundColor: "white",
            border: "2px solid #007bff",
            padding: "0.5rem 1rem",
            color: "#007bff",
            borderRadius: "8px",
            cursor: "pointer",
            fontWeight: "bold",
            fontSize: "1.5rem",
          }}
        >
          OddsGPT
        </button>
        <button
          style={{
            backgroundColor: "white",
            border: "2px solid #28a745",
            padding: "0.5rem 1rem",
            color: "#28a745",
            fontSize: "1rem",
            borderRadius: "8px",
            cursor: "pointer",
          }}
        >
          Home
        </button>

        {/* Model Status Indicator */}
        <div
          style={{
            marginLeft: "auto",
            display: "flex",
            alignItems: "center",
            gap: "1rem",
          }}
        >
          <div style={{ display: "flex", alignItems: "center", gap: "0.5rem" }}>
            <div
              style={{
                width: "12px",
                height: "12px",
                borderRadius: "50%",
                backgroundColor: getModelStatusColor(),
              }}
            ></div>
            <span
              style={{ color: "#333", fontSize: "0.9rem", fontWeight: "bold" }}
            >
              ESPN ML: {getModelStatusText()}
            </span>
          </div>

          {modelStatus &&
            (!modelStatus.model_available ||
              modelStatus.trained_sports?.length === 0) && (
              <button
                onClick={trainModel}
                style={{
                  backgroundColor: "#17a2b8",
                  color: "white",
                  border: "none",
                  padding: "0.5rem 1rem",
                  borderRadius: "6px",
                  cursor: "pointer",
                  fontSize: "0.9rem",
                  fontWeight: "bold",
                }}
              >
                Train Model
              </button>
            )}
        </div>
      </nav>

      <h1 style={{ textAlign: "center", color: "#333", marginBottom: "2rem" }}>
        ðŸ¤– AI Sports Betting App
      </h1>

      {/* Model Status Info */}
      {modelStatus && (
        <div
          style={{
            backgroundColor: "#f8f9fa",
            padding: "1rem",
            borderRadius: "8px",
            border: "1px solid #ddd",
            marginBottom: "2rem",
            textAlign: "center",
          }}
        >
          <strong>ðŸ† ESPN ML Model Status:</strong> {getModelStatusText()} |
          {modelStatus.trained_sports?.length > 0 && (
            <>
              <strong> Trained Sports:</strong>{" "}
              {modelStatus.trained_sports.join(", ")} |
            </>
          )}
          <strong> Data:</strong> {modelStatus.total_games || "N/A"} games from
          ESPN API
        </div>
      )}

      {/* Sport Selector */}
      <div style={{ marginBottom: "2rem" }}>
        <label
          style={{
            fontWeight: "bold",
            marginRight: "1rem",
            fontSize: "1.1rem",
          }}
        >
          Choose a sport:
        </label>
        <select
          onChange={(e) => setSportKey(e.target.value)}
          value={sportKey}
          style={{
            padding: "0.5rem",
            borderRadius: "6px",
            border: "2px solid #007bff",
            fontSize: "1rem",
            fontWeight: "bold",
            backgroundColor: "white",
          }}
        >
          <option value="basketball_nba">ðŸ€ NBA</option>
          <option value="americanfootball_nfl">ðŸˆ NFL</option>
          <option value="baseball_mlb">âš¾ MLB</option>
          <option value="icehockey_nhl">ðŸ’ NHL</option>
          <option value="soccer_epl">âš½ Soccer (EPL)</option>
        </select>
      </div>

      <h2 style={{ marginTop: "1rem", color: "#333" }}>Upcoming Games</h2>

      {/* Games List */}
      {games.length === 0 && (
        <div
          style={{
            marginTop: "2rem",
            textAlign: "center",
            padding: "3rem",
            backgroundColor: "#f8f9fa",
            borderRadius: "12px",
            border: "1px solid #ddd",
          }}
        >
          <h3 style={{ color: "#666", marginBottom: "0.5rem" }}>
            No Upcoming Games
          </h3>
          <p style={{ color: "#888", margin: 0 }}>
            Check back later for upcoming games and betting opportunities.
          </p>
        </div>
      )}

      <div
        style={{
          marginTop: "2rem",
          display: "grid",
          gap: "1.5rem",
          gridTemplateColumns: "repeat(auto-fit, minmax(280px, 1fr))",
        }}
      >
        {games.map((game, idx) => (
          <div
            key={idx}
            style={{
              backgroundColor: "#ffffff",
              border:
                selectedGame === game ? "2px solid #007bff" : "1px solid #ddd",
              borderRadius: "12px",
              boxShadow:
                selectedGame === game
                  ? "0 4px 12px rgba(0,123,255,0.3)"
                  : "0 2px 8px rgba(0,0,0,0.1)",
              padding: "1.5rem",
              display: "flex",
              flexDirection: "column",
              justifyContent: "space-between",
              transform: selectedGame === game ? "scale(1.02)" : "scale(1)",
              transition: "all 0.2s ease",
            }}
          >
            <div>
              <h3 style={{ marginBottom: "0.5rem", color: "#333" }}>
                {game.team_1} vs {game.team_2}
              </h3>
              <small style={{ color: "#666" }}>
                {new Date(game.date).toLocaleString()}
              </small>

              {game.odds && (
                <p style={{ marginTop: "1rem" }}>
                  <strong>Moneyline:</strong>
                  <br />
                  {game.team_1}: {game.odds[game.team_1]} <br />
                  {game.team_2}: {game.odds[game.team_2]}
                </p>
              )}

              {game.spreads && (
                <p style={{ marginTop: "0.5rem" }}>
                  <strong>Spread:</strong>
                  <br />
                  {game.team_1}: {game.spreads[game.team_1].point} (
                  {game.spreads[game.team_1].price}) <br />
                  {game.team_2}: {game.spreads[game.team_2].point} (
                  {game.spreads[game.team_2].price})
                </p>
              )}
            </div>

            <button
              style={{
                marginTop: "1rem",
                padding: "0.5rem",
                borderRadius: "6px",
                border: "none",
                backgroundColor: selectedGame === game ? "#28a745" : "#007bff",
                color: "#fff",
                cursor: "pointer",
                fontWeight: selectedGame === game ? "bold" : "normal",
                fontSize: selectedGame === game ? "1rem" : "0.9rem",
              }}
              onClick={() => setSelectedGame(game)}
            >
              {selectedGame === game ? "âœ“ Selected" : "Select"}
            </button>
          </div>
        ))}
      </div>

      {/* Prediction + ChatGPT */}
      {selectedGame && (
        <div
          style={{
            marginTop: "2rem",
            borderTop: "3px solid #007bff",
            paddingTop: "2rem",
            backgroundColor: "#f8f9fa",
            padding: "2rem",
            borderRadius: "12px",
          }}
        >
          <h2 style={{ color: "#333", marginBottom: "1rem" }}>
            ðŸŽ¯ Selected Game Analysis
          </h2>
          <p
            style={{ fontSize: "1.2rem", fontWeight: "bold", color: "#007bff" }}
          >
            <strong>{selectedGame.team_1}</strong> vs{" "}
            <strong>{selectedGame.team_2}</strong> (
            {sportKey.replace("_", " ").toUpperCase()})
          </p>

          {/* Action Buttons */}
          <div
            style={{
              marginBottom: "2rem",
              display: "flex",
              gap: "1rem",
              flexWrap: "wrap",
            }}
          >
            <button
              onClick={handlePredict}
              disabled={predictionLoading}
              style={{
                backgroundColor: predictionLoading ? "#6c757d" : "#28a745",
                color: "white",
                border: "none",
                padding: "0.75rem 1.5rem",
                borderRadius: "6px",
                cursor: predictionLoading ? "not-allowed" : "pointer",
                fontWeight: "bold",
                fontSize: "1rem",
                minWidth: "200px",
              }}
            >
              {predictionLoading
                ? "ðŸ¤– Analyzing..."
                : "ðŸŽ¯ Predict Winner (ESPN ML)"}
            </button>

            <button
              onClick={handleChatGPTAnalysis}
              style={{
                backgroundColor: "#17a2b8",
                color: "white",
                border: "none",
                padding: "0.75rem 1.5rem",
                borderRadius: "6px",
                cursor: "pointer",
                fontWeight: "bold",
                fontSize: "1rem",
                minWidth: "200px",
              }}
            >
              ðŸ’¬ Get ChatGPT Analysis
            </button>
          </div>

          {/* ML Prediction Results */}
          {prediction && (
            <div
              style={{
                marginTop: "1rem",
                backgroundColor: prediction.error ? "#f8d7da" : "#d4edda",
                padding: "1.5rem",
                borderRadius: "8px",
                border: prediction.error
                  ? "1px solid #f5c6cb"
                  : "1px solid #c3e6cb",
              }}
            >
              {prediction.error ? (
                <div style={{ color: "#721c24" }}>
                  <h3>âŒ Prediction Error</h3>
                  <p>
                    <strong>Error:</strong> {prediction.error}
                  </p>
                  {prediction.available_sports && (
                    <p>
                      <strong>Available Sports:</strong>{" "}
                      {prediction.available_sports.join(", ")}
                    </p>
                  )}
                  {prediction.available_teams_sample && (
                    <details style={{ marginTop: "1rem" }}>
                      <summary>
                        Available Teams Sample (click to expand)
                      </summary>
                      <ul style={{ marginTop: "0.5rem" }}>
                        {prediction.available_teams_sample.map((team, idx) => (
                          <li key={idx}>{team}</li>
                        ))}
                      </ul>
                    </details>
                  )}
                  {prediction.debug && (
                    <details style={{ marginTop: "1rem" }}>
                      <summary>Debug Info (click to expand)</summary>
                      <pre
                        style={{
                          fontSize: "0.8rem",
                          backgroundColor: "white",
                          padding: "1rem",
                          borderRadius: "4px",
                        }}
                      >
                        {JSON.stringify(prediction.debug, null, 2)}
                      </pre>
                    </details>
                  )}
                </div>
              ) : (
                <div style={{ color: "#155724" }}>
                  <h3>ðŸ¤– ESPN AI Prediction: {prediction.prediction}</h3>

                  <div
                    style={{
                      display: "grid",
                      gridTemplateColumns:
                        "repeat(auto-fit, minmax(200px, 1fr))",
                      gap: "1rem",
                      marginTop: "1rem",
                    }}
                  >
                    <div
                      style={{
                        backgroundColor: "white",
                        padding: "1rem",
                        borderRadius: "6px",
                        textAlign: "center",
                      }}
                    >
                      <strong>Confidence</strong>
                      <div
                        style={{
                          fontSize: "1.5rem",
                          fontWeight: "bold",
                          color: "#28a745",
                        }}
                      >
                        {((prediction.confidence || 0) * 100).toFixed(1)}%
                      </div>
                    </div>

                    <div
                      style={{
                        backgroundColor: "white",
                        padding: "1rem",
                        borderRadius: "6px",
                        textAlign: "center",
                      }}
                    >
                      <strong>Home Win Probability</strong>
                      <div
                        style={{
                          fontSize: "1.5rem",
                          fontWeight: "bold",
                          color: "#007bff",
                        }}
                      >
                        {((prediction.home_win_probability || 0) * 100).toFixed(
                          1
                        )}
                        %
                      </div>
                    </div>

                    <div
                      style={{
                        backgroundColor: "white",
                        padding: "1rem",
                        borderRadius: "6px",
                        textAlign: "center",
                      }}
                    >
                      <strong>Away Win Probability</strong>
                      <div
                        style={{
                          fontSize: "1.5rem",
                          fontWeight: "bold",
                          color: "#dc3545",
                        }}
                      >
                        {((prediction.away_win_probability || 0) * 100).toFixed(
                          1
                        )}
                        %
                      </div>
                    </div>
                  </div>

                  {prediction.model_info && (
                    <div
                      style={{
                        marginTop: "1rem",
                        backgroundColor: "white",
                        padding: "1rem",
                        borderRadius: "6px",
                      }}
                    >
                      <p style={{ margin: 0 }}>
                        <strong>Model:</strong> {prediction.model_info.type} |
                        <strong> Features Used:</strong>{" "}
                        {prediction.model_info.features_used || "80+"} |
                        <strong> Data Source:</strong> ESPN API (6,717+ games)
                      </p>
                    </div>
                  )}

                  {/* Betting Recommendation */}
                  {selectedGame.odds && (
                    <div
                      style={{
                        marginTop: "1rem",
                        backgroundColor: "#fff3cd",
                        padding: "1rem",
                        borderRadius: "6px",
                        border: "1px solid #ffeaa7",
                      }}
                    >
                      <h4 style={{ color: "#856404", margin: "0 0 0.5rem 0" }}>
                        ðŸ’° Quick Betting Analysis
                      </h4>
                      <p style={{ margin: 0, color: "#856404" }}>
                        {prediction.prediction.includes("home") ? (
                          <>
                            <strong>AI recommends:</strong>{" "}
                            {selectedGame.team_1} (Home) |
                            <strong> Current odds:</strong>{" "}
                            {selectedGame.odds[selectedGame.team_1]} |
                            <strong> AI confidence:</strong>{" "}
                            {((prediction.confidence || 0) * 100).toFixed(1)}%
                          </>
                        ) : (
                          <>
                            <strong>AI recommends:</strong>{" "}
                            {selectedGame.team_2} (Away) |
                            <strong> Current odds:</strong>{" "}
                            {selectedGame.odds[selectedGame.team_2]} |
                            <strong> AI confidence:</strong>{" "}
                            {((prediction.confidence || 0) * 100).toFixed(1)}%
                          </>
                        )}
                      </p>
                    </div>
                  )}
                </div>
              )}
            </div>
          )}

          {/* ChatGPT Analysis Results */}
          {chatGPTAnalysis && (
            <div
              style={{
                marginTop: "1rem",
                background: "#e3f2fd",
                padding: "1.5rem",
                borderRadius: "8px",
                border: "1px solid #bbdefb",
              }}
            >
              <h3 style={{ color: "#1565c0", margin: "0 0 1rem 0" }}>
                ChatGPT's Betting Analysis
              </h3>
              <div
                style={{
                  whiteSpace: "pre-wrap",
                  backgroundColor: "white",
                  padding: "1rem",
                  borderRadius: "6px",
                  color: "#333",
                  lineHeight: "1.6",
                }}
              >
                {chatGPTAnalysis}
              </div>
            </div>
          )}
        </div>
      )}

      {/* Debug Panel - Remove in production */}
      {(modelStatus || selectedGame || prediction) && (
        <details
          style={{ marginTop: "2rem", fontSize: "0.8rem", color: "#666" }}
        >
          <summary>ðŸ”§ Debug Info (click to expand)</summary>
          <pre
            style={{
              backgroundColor: "#f8f9fa",
              padding: "1rem",
              borderRadius: "6px",
              overflow: "auto",
              fontSize: "0.7rem",
            }}
          >
            {JSON.stringify(
              {
                modelStatus,
                selectedGame: selectedGame
                  ? {
                      team_1: selectedGame.team_1,
                      team_2: selectedGame.team_2,
                      sport: sportKey,
                    }
                  : null,
                prediction: prediction
                  ? {
                      prediction: prediction.prediction,
                      confidence: prediction.confidence,
                      error: prediction.error,
                    }
                  : null,
              },
              null,
              2
            )}
          </pre>
        </details>
      )}
    </div>
  );
}

export default App;
